package com.book.song.springboot.service.posts;import com.book.song.springboot.domain.posts.PostsRepository;import com.book.song.springboot.web.dto.PostsSaveRequestDto;import lombok.RequiredArgsConstructor;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;@RequiredArgsConstructor@Transactional(readOnly = true)@Servicepublic class PostsService {    private final PostsRepository postsRepository;    @Transactional    public Long save(PostsSaveRequestDto requestDto) {        return postsRepository.save(requestDto.toEntity())            .getId();    }}